<?phprequire_once '../general/funcionesGenerales.php';class clsCNMovimientos{    private $strDB='';    function setStrBD($str){        $this->strDB=$str;    }    function getStrBD(){        return $this->strDB;    }    function ListadoASPMovFilter($empresa,$strCuenta,$strPeriodo,$lngEjercicio,$datFecha,$datFechaFin,$strOperacion,$strCantidad,$lngCantidad,$strConcepto){		logger('traza','clsCNMovimientos.php-' ,"Usuario: ".$_SESSION['strUsuario'].', Empresa: '.$_SESSION['strBD'].', SesionID: '.  session_id().               " clsCNUsu->ListadoASPMovFilter($empresa,$strCuenta,$strPeriodo,$lngEjercicio,$datFecha,$datFechaFin,$strOperacion,$strCantidad,$lngCantidad,$strConcepto)");        require_once '../CAD/clsCADMovimientos.php';        $clsCADMov=new clsCADMovimientos();        $clsCADMov->setStrBD($this->getStrBD());        return $clsCADMov->ListadoASP_Mov($empresa,$strCuenta,$strPeriodo,$lngEjercicio,$datFecha,$datFechaFin,$strOperacion,$strCantidad,$lngCantidad,$strConcepto);    }    function ListadoASPMovAgruFilter($empresa,$strCuenta,$lngNumDigits,$strPeriodo,$lngEjercicio,$datFecha,$datFechaFin,$strOperacion,$strCantidad,$lngCantidad,$strConcepto){		logger('traza','clsCNMovimientos.php-' ,"Usuario: ".$_SESSION['strUsuario'].', Empresa: '.$_SESSION['strBD'].', SesionID: '.  session_id().               " clsCNUsu->ListadoASPMovFilter($empresa,$strCuenta,$lngNumDigits,$strPeriodo,$lngEjercicio,$datFecha,$datFechaFin,$strOperacion,$strCantidad,$lngCantidad,$strConcepto)");        require_once '../CAD/clsCADMovimientos.php';        $clsCADMov=new clsCADMovimientos();        $clsCADMov->setStrBD($this->getStrBD());        return $clsCADMov->ListadoASP_MovAgru($empresa,$strCuenta,$lngNumDigits,$strPeriodo,$lngEjercicio,$datFecha,$datFechaFin,$strOperacion,$strCantidad,$lngCantidad,$strConcepto);    }    function ListadoASPBalance($empresa,$strTipo,$strPeriodoD,$strPeriodoH,$lngEjercicio,$lngDigitos){		logger('traza','clsCNMovimientos.php-' ,"Usuario: ".$_SESSION['strUsuario'].', Empresa: '.$_SESSION['strBD'].', SesionID: '.  session_id().               " clsCNUsu->ListadoASPMovFilter($empresa,$strTipo,$strPeriodoD,$strPeriodoH,$lngEjercicio,$lngDigitos)");        require_once '../CAD/clsCADMovimientos.php';        $clsCADMov=new clsCADMovimientos();        $clsCADMov->setStrBD($this->getStrBD());        return $clsCADMov->ListadoASP_Balance($empresa,$strTipo,$strPeriodoD,$strPeriodoH,$lngEjercicio,$lngDigitos);    }        function SumasYSaldos(&$get){        logger('traza', 'clsCNMovimientos.php-', "Usuario: " . $_SESSION['strUsuario'] . ', Empresa: ' . $_SESSION['strBD'] . ', SesionID: ' . session_id() .                " clsCNMovimientos->SumasYSaldos()>");        require_once '../CAD/clsCADMovimientos.php';        $clsCADMovimientos = new clsCADMovimientos();        $clsCADMovimientos->setStrBD($this->getStrBD());        return $clsCADMovimientos->SumasYSaldos($get);    }}?>