<?phpsession_start();//carga la conexionrequire_once '../../general/'.$_SESSION['mapeo'];$db = new Db();$db->conectar($_SESSION['mapeo']);//cojemos los parametros de la URL$numP=  $_GET["numP"];$IdPedido = $_GET["IdPedido"];$query="SELECT NumPedido FROM tbmispedidos WHERE NumPedido='$numP' AND NOT IdPedido='$IdPedido' AND Borrado=1";$result=$db->ejecutar($query);if($result){    $row= mysql_num_rows($result);    if($row > 0){        $response="SI";    }else{        $response="NO";    }}else{    $response="NO";}//cerrar la conexion$db->desconectar();echo $response;?>