<?phpsession_start();//carga la conexionrequire_once '../../general/'.$_SESSION['mapeo'];$db = new Db();$db->conectar($_SESSION['mapeo']);$opcion = $_GET['opcion'];$dato = $_GET['dato'];//buscamos la opcion y el dato$query="SELECT $opcion FROM tbarticulos WHERE $opcion = '$dato' AND Borrado = 1";$result=$db->ejecutar($query);if($result){    $num = mysql_num_rows($result);    if($num > 0){        $response="SI";    }else{        $response="NO";    }}else{    $response="NO";}//cerrar la conexion$db->desconectar();//devuelvo la respuestaecho $response;?>