<?phpsession_start();//carga la conexion require_once '../../general/'.$_SESSION['mapeo'];$db = new Db();$db->conectar($_SESSION['mapeo']);//si hay menos de 5 digitos pongo ceros a la derechafunction formatearCodigo($codigo){    $long=strlen($codigo);    for($i=1;$i<=5-$long;$i++){        $codigo='0'.$codigo;    }    return $codigo;}//cojemos los parametros de la URL$codigo=formatearCodigo($_GET["cuenta"]);$tipo=$_GET["tipo"];$cuenta=$tipo.$codigo;//buscamos si hay nombre de usuario en la BD$query='SELECT * FROM tbcuenta WHERE NumCuenta="'.$cuenta.'" AND Borrado=0';$result=$db->ejecutar($query);if($result){    $count=  mysql_num_rows($result);    if($count>0){        $response="<img src='../images/error.png' width='15' height='15' />";    }else if(strlen($cuenta)==0){        $response="";    }else{        $response="<img src='../images/ok.png' width='15' height='15' />";    }}else{    $response="<img src='../images/ok.png' width='15' height='15' />";}////cerrar la conexion$db->desconectar();//devuelvo la respuesta echo $response;?>