<?phpsession_start();//carga la conexionrequire_once '../../general/conexion.php';$db = new DbC();$db->conectar($_SESSION['dbContabilidad']);//cojemos el parametro del fichero$file=$_GET["file"];//extraigo la extension$ext=explode('.',$file);$ext=$ext[1];$response='';//si no es PDF devuelvo el errorif(strtoupper($ext)<>'PDF'){    $response="<b class='fileError'>&nbsp;&nbsp;&nbsp;Este fichero NO es PDF</b>";}//creamos la URL donde se guarda$url="../doc/doc-".$_SESSION['base'].'/'.$file;//buscamos si hay nombre de fichero en la tabla tbconsulta_pregunta y tbconsulta_respuestas$query='SELECT strDoc FROM tbconsulta_pregunta WHERE strDoc="'.$url.'"';$result=$db->ejecutar($query);if($result){    $count=  mysql_num_rows($result);    if($count>0){        $response="<b class='fileError'>&nbsp;&nbsp;&nbsp;Nombre de fichero existe ya.<br/>&nbsp;&nbsp;&nbsp;Renombre el fichero por favor.</b>";    }}$query='SELECT strDoc FROM tbconsulta_respuestas WHERE strDoc="'.$url.'"';$result=$db->ejecutar($query);if($result){    $count=  mysql_num_rows($result);    if($count>0){        $response="<b class='fileError'>&nbsp;&nbsp;&nbsp;Nombre de fichero existe ya.<br/>&nbsp;&nbsp;&nbsp;Renombre el fichero por favor.</b>";    }}//cerrar la conexion$db->desconectar();//devuelvo la respuesta echo $response;?>