<?phpsession_start();//carga la conexionrequire_once '../../general/conexion.php';$db = new DbC();$db->conectar($_SESSION['dbContabilidad']);//cojemos el parametro 'q' de la URL$q=$_GET["q"];//buscamos si hay nombre de usuario en la BD$query='SELECT * FROM tbusuarios WHERE strUsuario="'.$q.'"';$result=$db->ejecutar($query);if($result){    $count=  mysql_num_rows($result);    if($count>0){        $response="<img src='../images/error.png' width='15' height='15' />";    }else if(strlen($q)==0){        $response="";    }else{        $response="<img src='../images/ok.png' width='15' height='15' />";    }}else{    $response="<img src='../images/ok.png' width='15' height='15' />";}////cerrar la conexion$db->desconectar();//devuelvo la respuesta al send que lo enviÃ³ en la pagina 'login.php'echo $response;?>