<?phpsession_start();//carga la conexionrequire_once '../../general/'.$_SESSION['mapeo'];//require_once '../../general/myLogger.php';$db = new Db();$db->conectar($_SESSION['mapeo']);//cojemos los parametros de la URLdate_default_timezone_set('Europe/Madrid');$fecha=  explode('/', $_GET["fecha"]);$periodo=$fecha[1];$ejercicio=$fecha[2];//buscamos si la fecha es cerrada o abierta, en la tabla tbperiodos_iva$query="SELECT Activo FROM tbperiodos_iva WHERE periodo=$periodo AND ejercicio=$ejercicio";$result=$db->ejecutar($query);if($result){    $row= mysql_fetch_array($result);        if($row['Activo']=='2' || $row['Activo']=='3'){        $response="CERRADO";    }else{        $response="ABIERTO";    }}else{    $response="ABIERTO";}////cerrar la conexion$db->desconectar();//devuelvo la respuesta al send que lo envió en la pagina 'login.php'echo $response;?>