<?phpclass departamentos{    public function buscarDepartamento($buscar,$bd){        require_once '../../general/conexion.php';        $db = new DbC();        $db->conectar($bd);        $datos=array();        $strSQL="SELECT strDescripcion FROM tbdepartamentos                    WHERE strDescripcion LIKE '%$buscar%'                     ORDER BY strDescripcion ASC";        $stmt = $db->ejecutar ( $strSQL );        $db->desconectar ();        while($row=  mysql_fetch_array($stmt,MYSQL_ASSOC)){            $datos[]=array("value"=>$row["strDescripcion"]);        }        return $datos;    }}$departamentos=new departamentos();echo json_encode($departamentos->buscarDepartamento($_GET['term'],$_GET['bd']));?>