<?phpsession_start();//carga la conexionrequire_once '../../general/'.$_SESSION['mapeo'];$db = new Db();$db->conectar($_SESSION['mapeo']);//cojemos el parametro 'q' de la URL$q=$_GET["q"];//consulta SQL$strSQL="SELECT CONCAT(NumCuenta , ' - ' , Nombre) AS cuentas,NumCuenta           FROM tbcuenta           WHERE CONCAT(NumCuenta , ' - ' , Nombre) LIKE '%".$q."%' AND Borrado=0           ORDER BY NumCuenta ASC";$result=$db->ejecutar($strSQL);//compruebo que haya valoresif($result){    $num=  mysql_num_rows($result);    if($num>0){        $response= '';    }else{        $response="NO";    }}else{    $response='';}//comprueba que haya algo introducido (que q tenga datos), sino devuelvo NOif($q===''){    $response='NO';}//cerrar la conexion$db->desconectar();//devuelvo la respuesta al send que lo envió echo $response;?>