<?phpsession_start();class cuentas{    public function buscarCuenta($buscar,$bd){        require_once '../../general/'.$_SESSION['mapeo'];        $db = new Db();        $db->conectar($bd);        $datos=array();        $strSQL="SELECT CONCAT(NumCuenta , ' - ' , Nombre) AS cuentas,NumCuenta                 FROM tbcuenta                 WHERE                 (CONCAT(NumCuenta , ' - ' , Nombre) LIKE '%".$buscar."%'                 AND (CONCAT(NumCuenta , ' - ' , Nombre) LIKE '4000%' OR CONCAT(NumCuenta , ' - ' , Nombre) LIKE '4100%')                 AND Borrado=0)                 OR                     (Sinonimos LIKE '%".$buscar."%'                 AND (CONCAT(NumCuenta , ' - ' , Nombre) LIKE '4000%' OR CONCAT(NumCuenta , ' - ' , Nombre) LIKE '4100%')                 AND Borrado=0)                 ORDER BY NumCuenta ASC";        $stmt = $db->ejecutar ( $strSQL );        $db->desconectar ();        while($row=  mysql_fetch_array($stmt,MYSQL_ASSOC)){            $datos[]=array("value"=>$row["cuentas"]);        }        return $datos;    }}$cuentas=new cuentas();echo json_encode($cuentas->buscarCuenta($_GET['term'],$_GET['bd']));?>