<?phpsession_start();//carga la conexionrequire_once '../../general/'.$_SESSION['mapeo'];require_once '../../general/myLogger.php';$db = new Db();$db->conectar($_SESSION['mapeo']);//cojemos los parametros 'q' y 'filtro' de la URL$q=$_GET["q"];$filtro=$_GET["filtro"];//consulta SQL$strSQL="SELECT CONCAT(NumCuenta , ' - ' , Nombre) AS cuentas,NumCuenta           FROM tbcuenta           WHERE CONCAT(NumCuenta , ' - ' , Nombre) = '".$q."'           AND CONCAT(NumCuenta , ' - ' , Nombre) LIKE '".$filtro."%'           ORDER BY NumCuenta ASC";$result=$db->ejecutar($strSQL);//compruebo que haya valoresif($result){    $num=  mysql_num_rows($result);    if($num>0){        $response= '';    }else{        $response="NO";    }}else{    $response='';}//cerrar la conexion$db->desconectar();//devuelvo la respuesta al send que lo envió echo $response;?>