<?phpsession_start();//carga la conexionrequire_once '../../general/conexion.php';$dbG = new DbC();//cojemos el parametro 'q' de la URL$claseEmpresa=$_GET['clase'];    //array donde guardo los datos$datos='';//hago el listado a la BBDD (solo usuarios, no asesores)$query="        SELECT EM.lngIdEmpleado        FROM tbempresas E, tbempleados EM,tbusuarios U        WHERE E.IdEmpresa=EM.IdEmpresa        AND EM.lngIdEmpleado=U.lngIdEmpleado        AND EM.lngStatus=1        AND E.claseEmpresa='$claseEmpresa'        AND U.strPuesto LIKE 'Usuario%'        ";$dbG->conectar($_SESSION['dbContabilidad']);$stmt=$dbG->ejecutar($query);$dbG->desconectar();if($stmt){    while($row=  mysql_fetch_array($stmt)){        $datos[]=$row['lngIdEmpleado'];    }}//devuelvo la respuesta al sendecho json_encode($datos);?>