<?phpsession_start();//carga la conexionrequire_once '../../general/conexion.php';require_once '../../general/myLogger.php';$db = new DbC();$db->conectar($_SESSION['dbContabilidad']);//cojemos el parametro 'q' de la URL$q=$_GET["q"];////extraemos los Nombres de empresas de la BD$query='SELECT * FROM tbempresas';$result=$db->ejecutar($query);//listo todas las empresas y las voy comparando con la vble. 'q'while($row=mysql_fetch_array($result)){	$a=mysql_real_escape_string($row['strNombre']);  if(strtolower($q)===strtolower($a)){  	$response="<img src='../images/ok.png' width='20' height='20' />";  	break;  }else{  	$response="<img src='../images/error.png' width='20' height='20' />";  }}////cerrar la conexion$db->desconectar();//devuelvo la respuesta al send que lo enviÃ³ en la pagina 'login.php'echo $response;?>