<?phpsession_start();//carga la conexionrequire_once '../../general/conexion.php';require_once '../../general/myLogger.php';$db = new DbC();$db->conectar($_SESSION['dbContabilidad']);//cojemos el parametro 'e' de la URL$e=$_GET["e"];//extraemos los Nombres de empresas de la BD$query='SELECT * FROM tbempresas WHERE strNombre="'.$e.'"';$result=$db->ejecutar($query);//cojemos el parametro 'q' de la URL$q=$_GET["q"];//listo todas las contraseñas y las voy comparando con la vble. 'q'while($row=mysql_fetch_array($result)){	$a=mysql_real_escape_string($row['strPassword']);	if($q===$a){		$response="<img src='../images/ok.png' width='20' height='20' />";	  	break;	}else{	  	$response="<img src='../images/error.png' width='20' height='20' />";	}}//cerrar la conexion$db->desconectar();//devuelvo la respuesta al send que lo envió en la pagina ''echo $response;?>