<?phpsession_start();//carga la conexionrequire_once '../../general/conexion.php';$dbT = new DbC();$dbT->conectar($_SESSION['dbContabilidad']);//extraemos los datos del usuario de la BD        //$query='//        SELECT * FROM tbusuarios WHERE strUsuario="'.$_GET["usuario"].'"//       ';//$query='//        SELECT P.strPuesto,U.strPassword//        FROM tbusuarios U,tbperfiles P, tbperfilempleado E//        WHERE U.lngIdEmpleado=E.lngIdEmpleado//        AND P.lngId=E.lngIdPerfil//        AND U.strUsuario="'.$_GET["usuario"].'"//       ';$query='        SELECT U.strPuesto,U.strPassword        FROM tbusuarios U,tbempleados E        WHERE U.lngIdEmpleado=E.lngIdEmpleado        AND U.strUsuario="'.$_GET["usuario"].'"       ';$result=$dbT->ejecutar($query);//cojemos el parametro 'clave' de la URL$clave=$_GET["clave"];//listo todas las contraseñas y las voy comparando con la vble. 'q'while($row=mysql_fetch_array($result)){    $a=mysql_real_escape_string($row['strPassword']);    if($clave===$a){        $Perfil=$row['strPuesto'];        //ahora veo si es asesor o cliente (en el campo lngPermiso<=20        if(substr($Perfil,0,6)==='Asesor'){            $response="<img src='../images/ok.png' width='20' height='20' id='asesor' />";            break;        }else{            $response="<img src='../images/ok.png' width='20' height='20' id='cliente' />";            break;        }    }else{        $response="<img src='../images/error.png' width='20' height='20' />";    }}//cerrar la conexion$dbT->desconectar();//devuelvo la respuesta al send que lo envío en la pagina 'login.php'echo $response;?>