<?phpsession_start();//carga la conexionrequire_once '../../general/conexion.php';//hago la conexion a la base de datos de la empresa (strMapeo)$dbT = new DbC();$dbT->conectar($_SESSION['dbContabilidad']);// ahora cojemos los parametros 'usuario' y empresa$usuario=$_GET["usuario"];//extraemos los Nombres de usuarios de la empresa de la BD dbContabilidad$query='SELECT * FROM tbusuarios';$result=$dbT->ejecutar($query);//listo todas los usuarios y los voy comparando con la vble. 'usuario'while($row=mysql_fetch_array($result)){	$a=mysql_real_escape_string($row['strUsuario']);  if(strtolower($usuario)===strtolower($a)){  	$response="<img src='../images/ok.png' width='20' height='20' />";  	break;  }else{  	$response="<img src='../images/error.png' width='20' height='20' />";  }}//cerrar la conexion$dbT->desconectar();//devuelvo la respuesta al send que lo envÃ­o en la pagina 'login.php'echo $response;?>