<?phpsession_start();//carga la conexionrequire_once '../../general/conexion.php';$db = new DbC();$db->conectar($_SESSION['dbContabilidad']);//cojemos el parametro 'q' de la URL$q=$_GET["q"];//buscamos si hay nombre de usuario en la BD$query='SELECT IdCliProv,Nombre,CIF,direccion,municipio,provincia,CP,Telefono1,Telefono2,Fax,Correo,Actividad,CNAE,NumSS        FROM tbcliprov  WHERE "' . $q . '" = CIF  AND Borrado = 0';$result=$db->ejecutar($query);if($result){    $count= mysql_num_rows($result);    if($count>0){        $datos='';        $row= mysql_fetch_array($result);//        $datos='IdCliProv='.$row['IdCliProv'].'&Nombre='.$row['Nombre'].'&CIF='.$row['CIF'].'&direccion='.$row['direccion'].//                '&municipio='.$row['municipio'].'&provincia='.$row['provincia'].'&CP='.$row['CP'].'&Telefono1='.//                $row['Telefono1'].'&Telefono2='.$row['Telefono2'].'&Fax='.$row['Fax'].'&Correo='.$row['Correo'].//                '&Actividad='.$row['Actividad'].'&CNAE='.$row['CNAE'].'&NumSS='.$row['NumSS'];        $datos=$row['IdCliProv'].'|'.$row['Nombre'].'|'.$row['CIF'].'|'.$row['direccion'].                '|'.$row['municipio'].'|'.$row['provincia'].'|'.$row['CP'].'|'.                $row['Telefono1'].'|'.$row['Telefono2'].'|'.$row['Fax'].'|'.$row['Correo'].                '|'.$row['Actividad'].'|'.$row['CNAE'].'|'.$row['NumSS'];        $response=$datos;    }else{        $response='vacio';    }}else{    $response='vacio';}////cerrar la conexion$db->desconectar();//devuelvo la respuesta al sendecho json_encode($response);?>