<?php//funcion AJAX que es llamada por 'consulta_del_asesor_proceso.php'session_start();require_once '../general/funcionesGenerales.php';require '../general/phpmailer/PHPMailerAutoload.php';require_once '../CN/clsCNUsu.php';$clsCNUsu=new clsCNUsu();$clsCNUsu->setStrBD($_SESSION['dbContabilidad']);$IdUsuario=$_GET['idUsuario'];$consulta=$_GET['consulta'];$asesor=$_GET['asesor'];$datosUsuarioActivo=$clsCNUsu->DatosUsuario($IdUsuario);$from="asesor@qualidad.es";$to=$datosUsuarioActivo['strCorreo'];$mail = new PHPMailer();//Correo desde donde se envía (from)$mail->setFrom($from, '');//Correo de envío (to)$mail->addAddress($to, '');$mail->CharSet = "UTF-8";$mail->Subject = "Consulta plataforma gestión online";$html='<!DOCTYPE html>        <html>            <head>                <title>Q-Conta</title>                <meta charset="UTF-8">                <meta name="viewport" content="width=device-width">            </head>            <body>                <div>Realizada por: '.$asesor.' de '.$_SESSION['sesion'].'</div>                <div>Consulta: '.$consulta.'</div><br/><br/><br/>                <div>                    <p>Esperamos que esta comunicación sea de utilidad para gestión de su empresa.</p>                    <p>Gracias por utilizar la plataforma de gestión online.</p>                    <p>Qualidad Consulting de Sistemas, S.L.</p>                </div><br/>            </body>        </html>';//Lee un HTML message body desde un fichero externo,//convierte HTML un plain-text básico $mail->msgHTML($html);if ($mail->send()) {    $respuesta=$respuesta.$datosUsuarioActivo['strNombre'].' '.$datosUsuarioActivo['strApellidos'].' ('.$datosUsuarioActivo['strCorreo'].')...OK<br>';} else {    $respuesta=$respuesta.$datosUsuarioActivo['strNombre'].' '.$datosUsuarioActivo['strApellidos'].' ('.$datosUsuarioActivo['strCorreo'].')...ERROR<br>';}echo $respuesta;?>