<?php//funcion AJAX que es llamada por 'contabilizar_facturas_proceso.php'session_start();require_once '../general/funcionesGenerales.php';require_once '../CN/clsCNContabilidad.php';$clsCNContabilidad=new clsCNContabilidad();$clsCNContabilidad->setStrBD($_SESSION['dbContabilidad']);$clsCNContabilidad->setStrBDCliente($_SESSION['mapeo']);$IdFactura=$_GET['idFactura'];//compruebo si esta insertado$asiento=$clsCNContabilidad->comprobarSiEstaInsertadoAsiento($IdFactura);$respuesta='Factura ';$datosFactura=$clsCNContabilidad->datosFactura($IdFactura);if($asiento==='0'){    //primero traigo los datos de la factura    $OK=$clsCNContabilidad->contabilizarAsiento($IdFactura,$datosFactura);    if($OK===TRUE){        $respuesta=$respuesta.$datosFactura['NumFactura'].'...OK<br>';    }else{        $respuesta=$respuesta.$datosFactura['NumFactura'].'...ERROR<br>';    }}else{    //el numero del asiento es distinto de 0 es que esta contabilizada    $respuesta=$respuesta.$datosFactura['NumFactura'].'...Est√° CONTABILIZADA<br>';}echo $respuesta;?>