rgoEquivalencia],RecargoEquivalencia,CodigoTransaccion,Deducible,Exclusion347,                Mediacion,CodigoActividad,Prorrateo)                VALUES                ($StatusTraspasadoConta,$Proceso,'$Aplicacion','$CodigoAsesor',$CodigoEmpresa,$Asiento,$OrdenMov,$OrdenIva,$CodigoIva,                 $BaseIva,$BaseCorrectora,$Iva,$CuotaIva,$PorcRecargoEquivalencia,$RecargoEquivalencia,$CodigoTransaccion,$Deducible,                 $Exclusion347,$Mediacion,'$CodigoActividad',$Prorrateo)                ";        if(!$dbAccess->conectar()){            echo '... ERROR';            die;        }        $rs = $dbAccess->consulta( $strSQL );        $dbAccess->desconectar();        if(!$rs){            echo '... ERROR';            die;        }    }}//por ultimo actualizo la tabla 'tbmovimientos' el campo 'Exportado'foreach($insertadoOK as $exportado){    $strSQL = "                UPDATE tbmovimientos                 SET Exportado=now()                WHERE IdMovimiento=".$exportado."             ";    $db->conectar($_SESSION['mapeo']);    $stmt = $db->ejecutar ( $strSQL );    $db->desconectar();}echo '... OK';echo '</td>';    echo '</tr>';    ?>