<?phpsession_start();//carga la conexionrequire_once '../general/funcionesGenerales.php';require_once '../general/'.$_SESSION['mapeo'];$db = new Db();$db->conectar($_SESSION['mapeo']);//cojemos los parametros de la URL$IdGrupo = $_GET["IdGrupo"];//buscamos el listado de los articulos de este grupo$query="        SELECT A.Id,A.Referencia,A.Descripcion,A.Precio,A.tipoIVA,        A.CantidadAlmacen,A.CuentaContable        FROM tbarticulos A, tbgrupoarticulos G        WHERE A.IdGrupo=G.IdGrupo        AND A.Borrado=1        AND G.IdGrupo=$IdGrupo        ";$stmt = $db->ejecutar($query);//cerrar la conexion$db->desconectar();//$response = "";if($stmt){    while($row = mysql_fetch_array($stmt)){        $reg='';        foreach($row as $propiedad => $valor){            if(!is_numeric($propiedad)){                $reg[$propiedad] = $valor;            }        }        $resultado[]=$reg;    }        $response = '<table id="datatablesMod" class="display">                <thead>                    <tr>                        <th>Referencia</th>                        <th>Descripción</th>                        <th>Precio</th>                        <th>Tipo IVA</th>                        <th>Cantidad Almacén</th>                        <th>Cuenta Contable</th>                        <th>Borrar</th>                    </tr>                </thead>                <tbody>';        for ($i = 0; $i < count($resultado); $i++) {        $link="javascript:document.location.href='../vista/altaArticulo.php?Id=".$resultado[$i]['Id']."';";                $response = $response . "<tr>";        $response = $response . '<td onclick="'.$link.'">'.$resultado[$i]['Referencia']."</td>";        $response = $response . '<td onclick="'.$link.'">'.$resultado[$i]['Descripcion']."</td>";        $response = $response . '<td onclick="'.$link.'">'.formateaNumeroContabilidad($resultado[$i]['Precio'])."</td>";        $response = $response . '<td onclick="'.$link.'">'.$resultado[$i]['tipoIVA']."</td>";        $response = $response . '<td onclick="'.$link.'">'.$resultado[$i]['CantidadAlmacen']."</td>";        $response = $response . '<td onclick="'.$link.'">'.$resultado[$i]['CuentaContable']."</td>";        $response = $response . "<td onclick=''></td>";        $response = $response . "</tr>";    }        $response = $response . "</tbody></table>";    }else{    $response = '';}//devuelvo la respuesta al send que lo envió en la pagina 'login.php'echo $response;?>